<?php/** * class AbstractController * @author William Bruno * @package wbruno 1.0beta 27/08/2010 */abstract class AbstractController{	protected $dao, $vo;	public function __construct(ValueObject $vo, AbstractModel $model) {		$this->vo = $vo;		$this->model = $model;	}	public function del()	{		$this->set_post();		$result = $this->model->del( $this->vo );		return $result;	}	public function save()	{		$this->set_post();		$result = $this->model->save( $this->vo );		return $result;	}	public function byId( $id )	{		$dados = $this->model->one((int)$id);		$this->set_dados( $dados );	}	public function set_dados( $dados )	{		if( is_object($dados) )		{			$_SESSION['error_msg'] = '';			$this->do_set_dados( $dados );		}		else			$_SESSION['error_msg'] = '<li class="red"><span class="ico"></span>				<strong class="system_title">Esse registro n√£o existe!</strong></li>';	}	abstract public function set_post();	abstract public function do_set_dados( $dados );}