<?php/** * @class AbstractModel * @author William Bruno * @date 08/09/2010 */abstract class AbstractModel{	protected $dao, $vo;  public function one( $id ) {    $sql = $this->dao->select( $this->vo )->byId( $id );    return $this->dao->query($sql);  }  public function all() {    $sql = $this->dao->select( $this->vo )->order();    return $this->dao->query( $sql );  }}