<?php/** * @class Project * @author William Bruno * @date 2014-08-19 */class Project extends AbstractModel{	public function __construct() {		$this->vo = new ProjectVO();    $this->dao = new DAO( 'project' );	}  public function all() {    $sql = $this->dao->select( $this->vo )->order('`order` ASC');    return $this->dao->query( $sql );  }}