<?php/** * @class UserController * @author William Bruno * @date 27/08/2010 */class UserController extends AbstractController{	public function set_post()	{		$this->vo->set_id( getPost('id') );		$this->vo->set_nome( getPost('name') );		$this->vo->set_user( getPost('user') );		$this->vo->set_pass( getPost('pass') );		$this->vo->set_level( getPost('level') );	}	public function do_set_dados( $dados )	{		$this->vo->set_id( $dados->id );		$this->vo->set_nome( $dados->label );		$this->vo->set_user( $dados->user );		$this->vo->set_pass( $dados->pass );		$this->vo->set_level( $dados->level );	}}